        <script>
            function addQuestionFunction(){
                var clone = $('.add-section-acord').clone().first();
                $('.add-question-div').last().append(clone);
            }

            function addSectionFunction() {
                var clone = $('.add-section-div').first().clone();
                    clone.find('.add-section-acord').remove();
                $('.main-section-div').last().append(clone);
            }

            function addNewPage(){
                var clone = $('.title-sec').last().clone();
                clone.find('.site-conducted').remove();
                clone.find('.conducted-on').remove();
                clone.find('.prepared-by').remove();
                clone.find('.location').remove();
                clone.find('.add-section-acord').remove();
                clone.find('.add-section-div').remove();
                $('.third_main_div_page').append(clone);
            }

            function removePage(){
                $('.title-sec').last().remove();
            }

            function removeSection(button) {
                $(button).closest('.add-section-div').remove();
            }

            function repeatSection(button) {
                if (button.checked) {
                    var clone = $(button).closest('.add-section-div').clone();
                    clone.find('input[type="text"]').val('');
                    clone.find('input[type="checkbox"]').prop('checked', false);
                    $(button).closest('.main-section-div').append(clone);
                }
            }

        </script>
